    private void crear_tarea_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_crear_tarea_btnActionPerformed
        id = 1;
        nombre = nombre_tarea.getText();
        descripcion = description_tarea.getText();
        prioridad = prioridad_box.getSelectedItem().toString();
        estado = "Pendiente";
        
         
       GestionTareas gestionTareas = new GestionTareas();
       Nueva_Tarea nuevatarea = new Nueva_Tarea();
       gestionTareas.agregarTarea(1,nombre, descripcion, prioridad,estado);
       
       Ver_Tareas vertareas = new Ver_Tareas();
      
        gestionTareas.mostrarTareas();
        
    }//GEN-LAST:event_crear_tarea_btnActionPerformed

